import React from "react";
import axios from 'axios';
import "../Converter/Converter.css"

const SECRECT_KEY = '76f5ea65dcc33674258094294818f787';

class Converter extends React.Component {
  state = {
    result: null,
    fromCurrency: "USD",
    toCurrency: "GBP",
    amount: 1,
    baseCurrency: 'SEK',
    currencies: [],
    currencyRates: []
  };


  // Initializes the currencies with values from the api
  componentDidMount() {
    axios.get(`http://data.fixer.io/api/latest?access_key=${SECRECT_KEY}`).then(response => {
      // Initialized with 'EUR' because the base currency is 'EUR'
      // and it is not included in the response
      const currencyAr = ["EUR"];
      let baseCurrencyVal;
      for (const key in response.data.rates) {
        baseCurrencyVal = response.data.rates[this.state.baseCurrency];
        currencyAr.push(key)
      }

      console.log('baseCurrencyVal', baseCurrencyVal)
      this.setState({
        baseCurrency: baseCurrencyVal,
        currencies: currencyAr.sort(),
        currencyRates: response.data.rates
      });
    })
      .catch(err => {
        console.log("Opps", err.message);
      });
  }

  // Event handler for the conversion
  convertHandler = () => {
    console.log('from ', this.state.fromCurrency)
    console.log('to ', this.state.toCurrency)
    console.log(this.state.baseCurrency)
    console.log(this.state.currencyRates)

    let toCurrencyVal;

    for (const key in this.state.currencyRates) {
      toCurrencyVal = response.data.rates[this.state.toCurrency];
    }

    console.log('toCurrencyVal', toCurrencyVal)


    const url = `http://data.fixer.io/api/latest?access_key=${SECRECT_KEY}&base=${this.state.fromCurrency}&symbols=${this.state.toCurrency}`;
    if (this.state.fromCurrency !== this.state.toCurrency) {
      axios
        .get(url)
        .then(response => {
          const result = this.state.amount * (response.data.rates[this.state.toCurrency]);
          this.setState({ result: result.toFixed(5) })
        })
        .catch(err => {
          console.log("Opps", err.message);
        });
    } else {
      this.setState({ result: "You cant convert the same currency!" })
    }
  };

  // Updates the states based on the dropdown that was changed
  selectHandler = (event) => {
    if (event.target.name === "from") {
      this.setState({ fromCurrency: event.target.value })
    }
    if (event.target.name === "to") {
      this.setState({ toCurrency: event.target.value })
    }
  }
  render() {
    return <div>
      <h2>I am converter</h2>

      <div className="Converter">
        <div className="Form">
          <input
            name="amount"
            type="text"
            value={this.state.amount}
            onChange={event =>
              this.setState({ amount: event.target.value })
            }
          />
          <select
            name="to"
            onChange={(event) => this.selectHandler(event)}
            value={this.state.toCurrency}
          >
            {this.state.currencies.map(cur => (
              <option key={cur}>{cur}</option>
            ))}
          </select>
          <button onClick={this.convertHandler}>Convert</button>
        </div>
        {this.state.result &&
          <h3>{this.state.result}</h3>
        }
      </div>
    </div>;
  }
}
export default Converter;
