import React from "react";
import axios from 'axios';
import "./styles.css";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      searchText: "",
      country: []
    };
  }
  onChangeHandle(event) {
    console.log('on change')
    this.setState({ searchText: event.target.value });
  }
  onSubmit(event) {
    event.preventDefault();
    const { searchText } = this.state;
    const url = `https://restcountries.eu/rest/v1/name/${searchText}?fullText=true`;
    axios.get(url).then(response => response.data)
      .then((data) => {
        console.log('Response', data)
        this.setState({ country: data });
      },
        error => {
          console.log('i am error')
        });
  }

  render() {
    let response = JSON.stringify(this.state.country);
    return (
      <div>
        <div>

        </div>
          

        <section class="results">
          <div class="country-info">
            <ul id="countries">
              {this.state.country.map(country => <li>{country.name} ,  {country.capital}, {country.population}, {country.currencies},  </li>)}
            </ul>
          </div>
        </section>
      </div>
    );
  }
}
export default App;
