import React from "react";
import axios from 'axios';
import "./styles.css";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      searchText: "",
      country: []
    };
  }
  onChangeHandle(event) {
    console.log('on change')
    this.setState({ searchText: event.target.value });
  }
  onSubmit(event) {
    event.preventDefault();
    const { searchText } = this.state;
    const url = `https://restcountries.eu/rest/v1/name/${searchText}?fullText=true`;
    axios.get(url).then(response => response.data)
      .then((data) => {
        console.log('Response', data)
        this.setState({ country: data });
      },
        error => {
          console.log('i am error')
        });
  }

  render() {
    let response = JSON.stringify(this.state.country);
    return (
      <div class="search-container">
        <div class="search-wrapper">
          <form onSubmit={event => this.onSubmit(event)}>
            <h2>Search Country</h2>
            <input
              id="countryName"
              placeholder="Enter Country Name"
              type="text"
              onChange={event => this.onChangeHandle(event)}
              value={this.state.searchText}
            />
            <button onClick={this.onSearch}>Search</button>
          </form>
        </div>

        <div class="content-wrapper">
          <h3>Country Details</h3>
          {
            this.state.country.map(country =>
              <div class="country-info">
                <span><label>Country Name</label>{country.name}</span>
                <span><label>Capital</label>{country.capital}</span>
                <span><label>Population</label>{country.population}</span>
                <span><label>Currencies</label>{country.currencies}</span>
              </div>)
          }
        </div>
      </div>
    );
  }
}
export default App;
