import React from "react";
import axios from 'axios';
import "./styles.css";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      searchText: "",
      country: []
    };
  }
  onChangeHandle(event) {
    this.setState({ searchText: event.target.value });
  }
  onSubmit(event) {
    event.preventDefault();

  }

  onSearch() {
    console.log('i am on click')
    const { searchText } = this.state;
    const url = `https://restcountries.eu/rest/v1/name/${searchText}?fullText=true`;
    axios.get(url).then(response => response.data)
      .then((data) => {
        console.log('Response', data)
        this.setState({ country: data });
      })
  }
  render() {
    let response = JSON.stringify(this.state.country);
    return (
      <div>
        <section class="search">
          <form onSubmit={event => this.onSubmit(event)}>
            <h2>Country search engine</h2>
            <input
              id="country-name"
              placeholder="India"
              type="text"
              onChange={event => this.onChangeHandle(event)}
              value={this.state.searchText}
            />
            <button onClick={this.onSearch}>Search</button>
          </form>
        </section>
        <section class="results">
          <div class="country-info">
            <ul id="countries">
              {this.state.country.map(country => <li>{country.name} ,  {country.capital}, {country.population}, {country.currencies},  </li>)}
            </ul>
          </div>
        </section>
      </div>
    );
  }
}
export default App;
