import React from "react";
import axios from 'axios';
import "../Converter/Converter.css"

const SECRECT_KEY = '76f5ea65dcc33674258094294818f787';

class Converter extends React.Component {
  state = {
    result: 0.00,
    toCurrency: "INR",
    amount: 1,
    baseCurrency: 'SEK',
    baseCurrencyValue: 0,
    currencies: [],
    currencyRates: []
  };


  // Initializes the currencies with values from the api
  componentDidMount() {
    axios.get(`http://data.fixer.io/api/latest?access_key=${SECRECT_KEY}`).then(response => {
      const currencyAr = [];
      let baseCurrencyVal;
      let defaultValue;
      for (const key in response.data.rates) {
        baseCurrencyVal = response.data.rates[this.state.baseCurrency];
        currencyAr.push(key)
      }
      this.setState({
        baseCurrencyValue: baseCurrencyVal,
        currencies: currencyAr.sort(),
        currencyRates: response.data.rates
      });
      this.convertHandler();
    })
      .catch(err => {
        console.log("Opps", err.message);
      });
    //console.log('baseValue', this.state.baseCurrencyValue)
  }

  onChangeHandle(e) {
    console.log('i am onchange')
    this.setState({ amount: e.target.value })
    this.convertHandler();
  }

  // Event handler for the conversion
  convertHandler = () => {
    console.log('this.state.toCurrency', this.state.baseCurrencyValue)
    //console.log('this.state.toCurrencyValue', this.state.toCurrencyValue)
    if (this.state.baseCurrency !== this.state.toCurrency) {
      let toCurrencyVal;
      for (const key in this.state.currencyRates) {
        toCurrencyVal = this.state.currencyRates[this.state.toCurrency];
      }

      console.log('this.state.amount', this.state.amount, this.state.baseCurrencyValue)
      let convertedValue = (this.state.amount / this.state.baseCurrencyValue) * toCurrencyVal;
      console.log('convertedValue', convertedValue)
      this.setState({ result: convertedValue ? convertedValue : 0 })

    } else {
      this.setState({ result: "You cant convert the same currency!" })
    }
  };

  // Updates the states based on the dropdown that was changed
  selectHandler = (event) => {
    if (event.target.name === "to") {
      this.setState({ toCurrency: event.target.value })
    }
  }
  render() {
    return <div>
      <h2>I am converter</h2>

      <div className="Converter">
        <div className="Form">
          <input
            name="amount"
            type="text"
            value={this.state.amount}
            onChange={event => this.onChangeHandle(event)}
          /* onChange={event =>
             this.setState({ amount: event.target.value })
           } */
          />
          <select
            name="to"
            onChange={(event) => this.selectHandler(event)}
            value={this.state.toCurrency}
          >
            {this.state.currencies.map(cur => (
              <option key={cur}>{cur}</option>
            ))}
          </select>
          <button onClick={this.convertHandler}>Convert</button>
          <input
            name="converted-amount"
            type="text"
            value={this.state.result}
          />
        </div>
        {this.state.result &&
          <h3>{this.state.result}</h3>
        }
      </div>
    </div>;
  }
}
export default Converter;
