import React from "react";
import axios from 'axios';
import "./styles.css";
import SearchCountry from "./atoms/SearchCountry/SearchCountry.js"
import Converter from "./atoms/Converter/Converter.js"

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      searchText: "",
      country: [],
      searchError: ""
    };
  }
  onChangeHandle(event) {
    this.setState({ searchText: event.target.value }, () => {
      this.validateSearchText();
    });
  }

  validateSearchText() {
    const { searchText } = this.state;
    const countryRegEx = new RegExp("^[a-zA-Z ]*$");
    this.setState({
      searchError: searchText < 2 && !countryRegEx.test(searchText) ? 'Enter Valid Country name' : null
    });
  }



  render() {
    return (
      <div className="container">
        <Converter />
        <SearchCountry />
      </div >

    );
  }
}
export default App;
